styles:
.shop-page-header {
  position: relative;
  position: -webkit-sticky;
  position: sticky;
  width: 100%;
  padding-top: 50%;
  background-color: #fff;
  background-size: cover;
  top: 0;
  z-index: 89;
}
.shop-page-header-mask {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(75,63,90,.7);
}
ion-avatar{
  width: 17%;
  height: 17%;
}
ion-item{
  margin: 7px;
  padding-top: 61px;
}
.color-text{
  color: white !important;
}

